import React, { Component } from 'react';
import ReactGA from 'react-ga';

import '../css/Home.css';
import logo from '../img/logo_white2.svg';
import mockups from '../img/mockups.png';

import ScrollIntoView from 'react-scroll-into-view'
import {Link} from "react-router-dom";

const isMobile = window.innerWidth <= 600;

/**
 * Event - Add custom tracking event.
 * @param {string} category
 * @param {string} action
 * @param {string} label
 */
export const Event = (category, action, label) => {
    ReactGA.event({
        category: category,
        action: action,
        label: label
    });
};
class Home extends Component {

    componentDidMount(){
        ReactGA.initialize('UA-141786654-1');
        ReactGA.pageview(window.location.pathname + window.location.search);
    }
    render() {
        return (
            <div className="Home">
                <div className="left">
                    <div className="logo">
                        <img src={logo} alt="logo"/>
                    </div>
                    <div className = "caption">
                        <p>
                            Your Hub for Classical Music
                        </p>
                    </div>
                    <div className = "info">
                        <p>
                            Livestreams, videos, and blogs from all your favorite artists, all in one place.
                        </p>
                    </div>
                    <div className="wrap">
                        <a style={{display: "table-cell", textDecoration: "none"}} href="https://danny235320.typeform.com/to/iJ2NhH" target="_blank">
                            <div className = "buttonBeta">
                                Join Our Beta
                            </div>
                        </a>
                        <Link to={'/purchase'} style={{ textDecoration: 'none' }}
                        onClick={() => {
                            Event("PURCHASE", "Product added to cart", "PURCHASE_PAGE")
                        }}>
                            <div className = "buttonInfo">
                                Preorder now!
                            </div>
                        </Link>
                    </div>

                </div>
                <img src = {mockups} className="mockups" />
                <ScrollIntoView selector="#info_copy_section">
                    <div className="scroll-down"></div>
                </ScrollIntoView>
            </div>
        );
    }
}

export default Home;
